import axios from 'axios';

const API_BASE_URL = 'http://localhost:8080/api';  // Replace with your Spring Boot backend URL

//export const addAccount = (accountData) => axios.post(`${API_BASE_URL}/accounts`, accountData);
export const getAccount = (accountId) => axios.get(`${API_BASE_URL}/accounts/${accountId}`);
export const depositAmount = (accountId, amount) => axios.put(`${API_BASE_URL}/accounts/${accountId}/deposit`, { amount });
export const withdrawAmount = (accountId, amount) => axios.put(`${API_BASE_URL}/accounts/${accountId}/withdraw`, { amount });
export const getAllAccounts = () => axios.get(`${API_BASE_URL}/accounts`);
export const deleteAccount = (accountId) => axios.delete(`${API_BASE_URL}/accounts/${accountId}`);

export const addAccount = (accountData) => {
    return axios.post(`${API_BASE_URL}/accounts`, {
        accountHolderName: accountData.account_holder_name,
        balance: accountData.balance
    });
};

export const login = (username, password) => {
    return axios.post(`${API_BASE_URL}/auth/login`, {
        username: username,
        password: password
    });
};

// New function to get accounts by user ID
export const getAccountsByUser = (userId) => {
    return axios.get(`${API_BASE_URL}/accounts/user/${userId}`);
};
