import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/ExternalTransfer.css';

function ExternalTransfer() {
    const [toRouting, setToRouting] = useState('');
    const [toAccount, setToAccount] = useState('');
    const [fromRouting, setFromRouting] = useState('');
    const [fromAccount, setFromAccount] = useState('');
    const [amount, setAmount] = useState('');
    const navigate = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();
        if (
            /^\d{1,16}$/.test(toRouting) &&
            /^\d{1,16}$/.test(toAccount) &&
            /^\d{1,16}$/.test(fromRouting) &&
            /^\d{1,16}$/.test(fromAccount) &&
            parseFloat(amount) > 0
        ) {
            // Perform transfer logic here
            alert(`External transfer of $${amount} submitted.`);
        } else {
            alert("Please fill out all fields correctly.");
        }
    };

    const handleCancel = () => {
        navigate(-1); // Go back to the previous page
    };

    return (
        <div>
            <h2>External Money Transfer</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>To Account Routing Number:</label>
                    <input
                        type="text"
                        value={toRouting}
                        onChange={(e) => setToRouting(e.target.value)}
                        pattern="\d{1,16}"
                        required
                    />
                </div>
                <div>
                    <label>To Account Number:</label>
                    <input
                        type="text"
                        value={toAccount}
                        onChange={(e) => setToAccount(e.target.value)}
                        pattern="\d{1,16}"
                        required
                    />
                </div>
                <div>
                    <label>From Account Routing Number:</label>
                    <input
                        type="text"
                        value={fromRouting}
                        onChange={(e) => setFromRouting(e.target.value)}
                        pattern="\d{1,16}"
                        required
                    />
                </div>
                <div>
                    <label>From Account Number:</label>
                    <input
                        type="text"
                        value={fromAccount}
                        onChange={(e) => setFromAccount(e.target.value)}
                        pattern="\d{1,16}"
                        required
                    />
                </div>
                <div>
                    <label>Amount:</label>
                    <input
                        type="number"
                        value={amount}
                        onChange={(e) => setAmount(e.target.value)}
                        min="0.01"
                        step="0.01"
                        required
                    />
                </div>
                <button type="submit">Submit</button>
                <button type="button" onClick={handleCancel}>Cancel</button>
            </form>
        </div>
    );
}

export default ExternalTransfer;
