import axios from 'axios';

const API_BASE_URL = 'http://localhost:8080/api';  // Replace with your Spring Boot backend URL

export const addAccount = (accountData) => axios.post(`${API_BASE_URL}/accounts`, accountData);
export const getAccount = (accountId) => axios.get(`${API_BASE_URL}/account/${accountId}`);
export const depositAmount = (accountId, amount) => axios.put(`${API_BASE_URL}/account/${accountId}/deposit`, { amount });
export const withdrawAmount = (accountId, amount) => axios.put(`${API_BASE_URL}/account/${accountId}/withdraw`, { amount });
export const getAllAccounts = () => axios.get(`${API_BASE_URL}/accounts`);
export const deleteAccount = (accountId) => axios.delete(`${API_BASE_URL}/account/${accountId}`);
