import React, { useState, useEffect } from 'react';
import { getAllAccounts, getAccountsByUser } from '../services/api';
import NavigationButtons from './NavigationButtons';

function AccountList() {
    const [accounts, setAccounts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [viewSingleAccount, setViewSingleAccount] = useState(null);  // Track if viewing single account

    useEffect(() => {
        const userRole = localStorage.getItem('userRole');
        const userId = localStorage.getItem('userId');

        async function fetchAccounts() {
            setLoading(true);
            try {
                let response;
                if (userRole === 'BankManager') {
                    response = await getAllAccounts();
                } else if (userRole === 'User' && userId) {
                    response = await getAccountsByUser(userId);
                }

                if (response) {
                    setAccounts(response.data);
                    setError(null);
                } else {
                    setError("No accounts available for this user.");
                }
            } catch (error) {
                console.error("Error fetching accounts:", error);
                setError("Failed to load accounts.");
            } finally {
                setLoading(false);
            }
        }

        fetchAccounts();
    }, []);

    const handleViewDetails = (account) => {
        setViewSingleAccount(account);
    };

    const handleBackToAllAccounts = () => {
        setViewSingleAccount(null);  // Return to the full account list
    };

    if (loading) {
        return <div>Loading accounts...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="account-list">
            <NavigationButtons
                showBackToAll={!!viewSingleAccount}
                onBackToAll={handleBackToAllAccounts}
            />
            <h2>Accounts</h2>
            {viewSingleAccount ? (
                <div className="account-card">
                    <h3>{viewSingleAccount.account_holder_name || "Account Holder"}</h3>
                    <p><strong>Account ID:</strong> {viewSingleAccount.id}</p>
                    <p><strong>Balance:</strong> ${viewSingleAccount.balance}</p>
                    {/* Add more details if needed */}
                </div>
            ) : (
                <div className="account-cards">
                    {accounts.map(account => (
                        <div className="account-card" key={account.id}>
                            <h3>{account.account_holder_name || "Account Holder"}</h3>
                            <p><strong>Account ID:</strong> {account.id}</p>
                            <p><strong>Balance:</strong> ${account.balance}</p>
                            <div className="actions">
                                <button onClick={() => handleViewDetails(account)} className="view-button">
                                    View Details
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default AccountList;
