import React, { useState } from 'react';
import { addAccount } from './api';

function AddAccount() {
    const [form, setForm] = useState({ name: '', initialBalance: '' });

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        addAccount(form).then(() => {
            alert('Account added successfully');
            setForm({ name: '', initialBalance: '' });
        });
    };

    return (
        <div>
            <h2>Add Account</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" name="name" value={form.name} onChange={handleChange} placeholder="Name" required />
                <input type="number" name="initialBalance" value={form.initialBalance} onChange={handleChange} placeholder="Initial Balance" required />
                <button type="submit">Add Account</button>
            </form>
        </div>
    );
}

export default AddAccount;
