import React, { useState, useEffect } from 'react';
import { getTransactionsByAccountId } from '../services/api';  // Updated import

function TransactionDetails({ accountId }) {
    const [transactions, setTransactions] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        console.log("Transaction Details for Account ID:", accountId);
        async function fetchTransactions() {
            setLoading(true);
            try {
                const response = await getTransactionsByAccountId(accountId);
                setTransactions(response.data);
            } catch (error) {
                console.error("Error fetching transactions:", error);
                setError("Failed to load transactions.");
            } finally {
                setLoading(false);
            }
        }

        if (accountId) {
            fetchTransactions();
        }
    }, [accountId]);


    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div>
            <h3>Transaction Details</h3>
            {transactions.length > 0 ? (
                <ul>
                    {transactions.map(transaction => (
                        <li key={transaction.id}>
                            <p><strong>Date:</strong> {transaction.date}</p>
                            <p><strong>Description:</strong> {transaction.description}</p>
                            <p><strong>Amount:</strong> ${transaction.amount}</p>
                            <p><strong>Type:</strong> {transaction.transactionType}</p>
                            <p><strong>Balance Before:</strong> ${transaction.balanceBefore}</p>
                            <p><strong>Balance After:</strong> ${transaction.balanceAfter}</p>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No transactions available for this account.</p>
            )}
        </div>
    );
}

export default TransactionDetails;
