import React, { useState, useEffect } from 'react';
// In AccountDetails.js, AccountList.js, and AddAccount.js
import { getAllAccounts, deleteAccount } from '../services/api';
import '../styles/AccountList.css';

function AccountList() {
    const [accounts, setAccounts] = useState([]);

    useEffect(() => {
        // Retrieve user_role and username from cookies
        const user_role = document.cookie
            .split('; ')
            .find(row => row.startsWith('user_role='))
            ?.split('=')[1];
        const username = document.cookie
            .split('; ')
            .find(row => row.startsWith('username='))
            ?.split('=')[1];

        async function fetchAccounts() {
            setLoading(true);
            try {
                let response;
                if (user_role === 'BankManager') {
                    // Fetch all accounts if the user is a BankManager
                    response = await getAllAccounts();
                } else if (username) {
                    // Fetch only accounts associated with the logged-in username
                    response = await getAccountsByUsername(username);
                }

                if (response) {
                    setAccounts(response.data);
                    setError(null);
                } else {
                    setError("No accounts available for this user.");
                }
            } catch (error) {
                console.error("Error fetching accounts:", error);
                setError("Failed to load accounts.");
            } finally {
                setLoading(false);
            }
        }

        fetchAccounts();
    }, []);

    const handleDelete = (accountId) => {
        deleteAccount(accountId)
            .then(() => setAccounts(accounts.filter(account => account.id !== accountId)))
            .catch(error => console.error("Error deleting account:", error));
    };

    return (
        <div className="account-list">
            <h2>Accounts</h2>
            <div className="account-cards">
                {accounts.map(account => (
                    <div className="account-card" key={account.id}>
                        <h3>{account.account_holder_name}</h3>
                        <p><strong>Account ID:</strong> {account.id}</p>
                        <p><strong>Balance:</strong> ${account.balance}</p>
                        <div className="actions">
                            <button onClick={() => handleDelete(account.id)} className="delete-button">Delete</button>
                            <button className="view-button">View Details</button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default AccountList;
