import React, { useState, useEffect } from 'react';
import { getAllAccounts, deleteAccount } from './api';

function AccountList() {
    const [accounts, setAccounts] = useState([]);

    useEffect(() => {
        getAllAccounts().then(response => setAccounts(response.data));
    }, []);

    const handleDelete = (accountId) => {
        deleteAccount(accountId).then(() => setAccounts(accounts.filter(acc => acc.id !== accountId)));
    };

    return (
        <div>
            <h2>All Accounts</h2>
            <ul>
                {accounts.map(account => (
                    <li key={account.id}>
                        {account.name} - Balance: ${account.balance}
                        <button onClick={() => handleDelete(account.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default AccountList;
