// src/components/AddAccount.js

import React, { useState } from 'react';
import { addAccount } from '../services/api';
import './AddAccount.css';

function AddAccount() {
    const [form, setForm] = useState({ name: '', initialBalance: '' });
    const [message, setMessage] = useState('');

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        addAccount(form)
            .then(() => {
                setMessage('Account added successfully!');
                setForm({ name: '', initialBalance: '' });
            })
            .catch(error => {
                console.error("Error adding account:", error);
                setMessage('Failed to add account. Please try again.');
            });
    };

    return (
        <div className="add-account">
            <h2>Add New Account</h2>
            {message && <p className="message">{message}</p>}
            <form onSubmit={handleSubmit} className="account-form">
                <div className="form-group">
                    <label htmlFor="name">Account Name</label>
                    <input
                        type="text"
                        id="name"
                        name="name"
                        value={form.name}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="initialBalance">Initial Balance</label>
                    <input
                        type="number"
                        id="initialBalance"
                        name="initialBalance"
                        value={form.initialBalance}
                        onChange={handleChange}
                        required
                    />
                </div>
                <button type="submit" className="submit-button">Add Account</button>
            </form>
        </div>
    );
}

export default AddAccount;
