import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/LandingPage.css';
import { getAccountsByUser } from '../services/api';
import useLogout from '../hooks/useLogout';

function UserLandingPage() {
    const [accounts, setAccounts] = useState([]);
    const logout = useLogout();

    useEffect(() => {
        async function fetchAccounts() {
            // Retrieve user ID from localStorage (or another secure storage if set elsewhere)
            const userId = localStorage.getItem('userId');

            if (userId) {
                try {
                    // Call the API to get accounts for the specific user
                    const response = await getAccountsByUser(userId);
                    setAccounts(response.data);
                } catch (error) {
                    console.error("Error fetching accounts:", error);
                }
            } else {
                console.error("User ID not found in local storage");
            }
        }

        fetchAccounts();
    }, []);

    return (
        <div>
            <h1>Your Accounts</h1>
            <button className="button" onClick={logout}>Logout</button>
            <ul>
                {accounts.map(account => (
                    <li key={account.id}>
                        Account ID: {account.id}, Balance: ${account.balance}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default UserLandingPage;
